import pandas as pdimport matplotlib.pyplot as pltimport seaborn as snsdf = pd.read_csv("logs/winner_stats.csv")df["label"] = df["label"].astype(str)plt.figure(figsize=(8, 5))sns.barplot(data=df, x="label", y="num_winners", palette="Blues_d")plt.title("Số lượng winners theo từng chữ số (ảnh xác định)")plt.xlabel("Chữ số")plt.ylabel("Số lượng winners")plt.tight_layout()plt.savefig("logs/winner_barplot.png")plt.show()# In bảng thống kêsummary = df.groupby("label")["num_winners"].agg(["mean", "std"])print(summary)